import React, { useState } from 'react'
import './EmployeeDetail.css';

function EmployeeDetail(props) {
const [salary, setSalary] = useState(0);
    const  totalCostPerEmployee = props.costPerperson + salary;
    const profit = totalCostPerEmployee * (props.profitPercent / 100);
    function c() {
        console.log(totalCostPerEmployee, profit);
    }
    return (
    <div>
        <label>Employee Name</label>
        <input type='text'/>
        <label>Employee Salary</label>
        <input type='number' onChange={(e) => setSalary(e.target.value)}/>
    </div>
    );
}

export default EmployeeDetail;
