import React, { useState } from 'react'
import './Setting.css';


function Setting({ totalAmount }) {
const [employeeNumber, setEmployeeNumber] = useState(1);
const [profitPercent, setProfitPercent] = useState(100);

handleEmployeeNumberChange = (e) => {
setEmployeeNumber(e.target.value);
const costPerPerson = totalAmount / employeeNumber;
}

return (
    <div className='settingForm'>
        <input type='number' name="employeeNumber" value={employeeNumber} onChange= { handleEmployeeNumberChange }/>
        <input type='number' name='profitPercent' value={profitPercent} onChange={(e) => setProfitPercent(e.target.value)}/>
    </div>
);

}

export default Setting;
